SELECT tep.*, tc.clazz_name, tepa.exam_paper_title, tepa.exam_paper_total_scroe, tu.username FROM ( SELECT * FROM t_exam_publish WHERE `status` = 1 ) tep LEFT JOIN ( SELECT * FROM t_clazz WHERE `status` = 1 ) tc ON tep.clazz_id = tc.id LEFT JOIN ( SELECT * FROM t_exam_paper WHERE `status` = 1 ) tepa ON tep.exam_id = tepa.id LEFT JOIN ( SELECT * FROM t_user WHERE `status` = 1 ) tu ON tep.publish_person = tu.id ORDER BY tep.start_time DESC
